    import Linear exposing (DirectionLinear(..))
    import Stack exposing (topDown)

    Scroll.only 234
        |> Scroll.sideAlter
            ( Up, \_ -> topDown 345 [ 543 ] )
        |> Scroll.fold Up max
    --> 543

